import React, { useContext } from 'react';
import PropTypes from 'prop-types';
import MovieContext from '../../context/movie/movieContext';

const MovieItem = ({ movie }) => {
  const movieContext = useContext(MovieContext);
  const { deleteMovie, setCurrent, clearCurrent } = movieContext;
  const { id, Title, Cast, Director, Genre, OriginEthnicity, Plot, ReleaseYear, WikiPage} = movie;

  const onDelete = () => {
    deleteMovie(id);
    clearCurrent();
  };

  return (
    <div className='card bg-light'>
      <h3 className='text-primary text-left'>{Title}</h3>
      <ul className='list'>{type && <li>{ReleaseYear}</li>}</ul>
      <ul className='list'>{type && <li>{Cast}</li>}</ul>
      <ul className='list'>{type && <li>{Director}</li>}</ul>
      <ul className='list'>{type && <li>{Genre}</li>}</ul>
      <ul className='list'>{type && <li>{OriginEthnicity}</li>}</ul>
      <ul className='list'>{type && <li>{Plot}</li>}</ul>
      <ul className='list'>{type && <li>{WikiPage}</li>}</ul>
      <p>
        <button
          className='btn btn-dark btn-sm'
          onClick={() => setCurrent(movie)}
        >
          Edit
        </button>
        <button className='btn btn-danger btn-sm' onClick={onDelete}>
          Delete
        </button>
      </p>
    </div>
  );
};

MovieItem.propTypes = {
  movie: PropTypes.object.isRequired,
};
export default MovieItem;
