import React, { useState } from 'react';

const WorkoutForm = () => {
  const [workout, setWorkout] = useState({
    name: '',
    type: '',
  });

  const { name, type } = workout;

  const onChange = (e) =>
    setWorkout({ ...workout, [e.target.name]: e.target.value });

  return (
    <form>
      <h2 className='text-primary'> Add Workout</h2>
      <input
        type='text'
        placeholder='Name'
        name='name'
        value={name}
        onchange={onChange}
      />
      <input
        type='text'
        placeholder='Type'
        name='type'
        value={type}
        onchange={onChange}
      />
      <div>
        <input
          type='submit'
          value='Add Workout'
          className='btn btn-primary btn-block'
        />
      </div>
    </form>
  );
};

export default WorkoutForm;
